import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
import Stack from "react-bootstrap/Stack";
import Button from "react-bootstrap/Button";
import "./bootstrap.css";

export default class TodoApp extends Component {
  render() {
    return (
      <div className="TodoApp">
        <Router>
          <HeaderComponent />
          <Switch>
            <Route path="/" exact component={LoginComponent} />
            <Route path="/login" component={LoginComponent} />
            <Route path="/dashboard/:name" component={DashboardComponent} />
            <Route path="/sdslibrary" component={SDSLibraryComponent} />
            <Route path="/logout" component={LogoutComponent} />
            <Route component={ErrorComponent} />
          </Switch>
          <FooterComponent />
        </Router>
        {/* <LoginComponent /> 
        <WelcomeComponent/> */}
      </div>
    );
  }
}

class HeaderComponent extends Component {
  render() {
    return (
      <header>
        <nav className="navbar navbar-expand-md navbar-dark bg-primary">
          <div>
            <a className="navbar-brand">BVM PRA</a>
          </div>
          <ul className="navbar-nav">
            <li>
              <Link className="nav-link" to="/dashboard/tester">
                Dashboard
              </Link>
            </li>
            <li>
              <Link className="nav-link" to="/sds">
                SDS
              </Link>
            </li>
          </ul>
          <ul className="navbar-nav navbar-collapse justify-content-end">
            <li>
              <Link className="nav-link" to="/login">
                Login
              </Link>
            </li>
            <li>
              <Link className="nav-link" to="/logout">
                Logout
              </Link>
            </li>
          </ul>
        </nav>
      </header>
    );
  }
}

class LogoutComponent extends Component {
  render() {
    return (
      <div>
        <h1>You are looged out of the PRM system</h1>
        <div className="container">Enjoy Your Day</div>
      </div>
    );
  }
}

class FooterComponent extends Component {
  render() {
    return (
      <footer className="footer">
        <span className="text-muted">
          All Rights Reserved 2021 BVM Product Risk Management
        </span>
      </footer>
    );
  }
}

class SDSLibraryComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [
        {
          id: 1,
          description: "Learn React",
          done: false,
          targetDate: new Date(),
        },
        {
          id: 2,
          description: "Learn eat",
          done: false,
          targetDate: new Date(),
        },
        { id: 3, description: "farm big", done: false, targetDate: new Date() },
      ],
    };
  }

  render() {
    return (
      <div>
        <h1>SAFETY DATA SHEETS</h1>
        <div className="container">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Description</th>
                <th>Done</th>
                <th>Target Date</th>
              </tr>
            </thead>
            <tbody>
              {this.state.todos.map((todo) => (
                <tr>
                  <td>{todo.id}</td>
                  <td>{todo.description}</td>
                  <td>{todo.done.toString()}</td>
                  <td>{todo.targetDate.toString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

class ErrorComponent extends Component {
  render() {
    return <div>An error occured.</div>;
  }
}

class DashboardComponent extends Component {
  render() {
    return (
      <div>
        <h1>DASHBOARD</h1>
        <div className="container">
          <h2>Product Classification</h2>
          <button>x</button>
          {/* Welcome {this.props.match.params.name}. You can manage the safety data sheets{" "}
          <Link to="/todos">here</Link> */}
        </div>
        <div className="container">
          <h2>Safety Data Sheets</h2>
          <button className="btn btn-primary mr-1">New SDS Form</button>
          <button className="btn btn-primary mr-1"> Edit SDS Form</button>
          <button className="btn btn-primary mr-1">Generate SDS Report</button>
          <button
            className="btn btn-primary mr-1"
            onClick={this.SDSLibraryClicked}
          >
            View SDS Library
          </button>
          {/* Welcome {this.props.match.params.name}. You can manage the safety data sheets{" "}
          <Link to="/todos">here</Link> */}
        </div>
        <div className="container">
          <h2>Regulatory Information</h2>
          <button>x</button>
          {/* Welcome {this.props.match.params.name}. You can manage the safety data sheets{" "}
          <Link to="/todos">here</Link> */}
        </div>
        <div className="container">
          <h2>Product / Chemical Risk</h2>
          <button>x</button>
          {/* Welcome {this.props.match.params.name}. You can manage the safety data sheets{" "}
          <Link to="/todos">here</Link> */}
        </div>
      </div>
    );
  }
  SDSLibraryClicked() {
    //tester,helloworld
    this.props.history.push(`/sdslibrary/tester`);
    // this.setState({ showSuccessMessage: true });
    // this.setState({ hasLoginFailed: false });
  }
}

class LoginComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "tester",
      password: "",
      hasLoginFailed: false,
      showSuccessMessage: false,
    };
    this.handleChange = this.handleChange.bind(this);
    this.loginClicked = this.loginClicked.bind(this);
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  loginClicked() {
    //tester,helloworld
    if (
      this.state.username === "tester" &&
      this.state.password === "helloworld"
    ) {
      this.props.history.push(`/welcome/${this.state.username}`);
      // this.setState({ showSuccessMessage: true });
      // this.setState({ hasLoginFailed: false });
    } else {
      this.setState({ showSuccessMessage: false });
      this.setState({ hasLoginFailed: true });
    }
  }

  render() {
    return (
      <div>
        <h1>Login</h1>
        <div className="container">
          {/* <ShowLoginMessage
          hasLoginFailed={this.state.hasLoginFailed}
          showSuccessMessage={this.state.showSuccessMessage}
        /> */}
          {this.state.hasLoginFailed && (
            <div className="alert alert-warning">Invalid Credentials</div>
          )}
          {/* {this.state.showSuccessMessage && <div>Login Successful</div>} */}
          User name:
          <input
            type="text"
            name="username"
            value={this.state.username}
            onChange={this.handleChange}
          />
          Password:
          <input
            type="password"
            name="password"
            value={this.state.password}
            onChange={this.handleChange}
          />
          <button className="btn btn=s" onClick={this.loginClicked}>
            Login
          </button>
        </div>
      </div>
    );
  }
}

